import React, { useState, useEffect } from 'react';
import { Modal, Button, View, Text, TextInput, FlatList } from 'react-native';
import axios from 'axios';

interface Product {
  id?: number;
  name: string;
  description: string;
  price: number;
  id_subcategory: number;
  img: string; // Cambiado a string para la URL de la imagen
}

const Crud: React.FC = () => {
  const [showModal, setShowModal] = useState(false);
  const [editProductId, setEditProductId] = useState<number | null>(null);
  const [product, setProduct] = useState<Product>({
    name: '',
    description: '',
    price: 0,
    id_subcategory: 0,
    img: '',
  });
  const [products, setProducts] = useState<Product[]>([]);
  const [errors, setErrors] = useState<Partial<Product>>({}); // Nuevo estado para mensajes de error
  const [formValid, setFormValid] = useState<boolean>(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        await fetchProducts();
      } catch (error) {
        console.error('Error al obtener productos:', error);
      }
    };

    fetchData();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get<Product[]>('http://localhost/ProyePet/public/api/Product_index');
      setProducts(response.data);
    } catch (error) {
      console.error('Error al obtener productos:', error);
    }
  };

  const handleSubmit = async () => {
    // Lógica para manejar el envío del formulario
  };

  const handleDeleteProduct = async (productId: number) => {
    // Lógica para manejar la eliminación de un producto
  };

  const handleEditProduct = (productId: number) => {
    // Lógica para manejar la edición de un producto
  };

  const handleUpdateProduct = async () => {
    // Lógica para manejar la actualización de un producto
  };

  return (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
      <Button title="Agregar Producto" onPress={() => setShowModal(true)} />

      <Modal visible={showModal} onRequestClose={() => setShowModal(false)}>
        <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
          <Text>{editProductId ? 'Editar' : 'Agregar'} Producto</Text>
          <TextInput
            placeholder="Nombre"
            value={product.name}
            onChangeText={(text) => setProduct({ ...product, name: text })}
            style={{ borderBottomWidth: 1, marginBottom: 10 }}
          />
          <TextInput
            placeholder="Descripción"
            value={product.description}
            onChangeText={(text) => setProduct({ ...product, description: text })}
            style={{ borderBottomWidth: 1, marginBottom: 10 }}
          />
          {/* Agregar más campos de entrada según sea necesario */}
          <Button title={editProductId ? 'Actualizar Producto' : 'Agregar Producto'} onPress={handleSubmit} />
          <Button title="Cerrar" onPress={() => setShowModal(false)} />
        </View>
      </Modal>

      <FlatList
        data={products}
        renderItem={({ item }) => (
          <View>
            <Text>{item.name}</Text>
            <Button title="Eliminar" onPress={() => handleDeleteProduct(item.id)} />
            <Button title="Editar" onPress={() => handleEditProduct(item.id)} />
          </View>
        )}
        keyExtractor={(item) => item.id?.toString()}
      />
    </View>
  );
};

export default Crud;
