import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Modal, Button, Form, Table } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

interface Product {
  id?: number;
  name: string;
  description: string;
  price: number;
  id_subcategory: number;
  img: File | null;
}

const Crud: React.FC = () => {
  const [showModal, setShowModal] = useState(false);
  const [editProductId, setEditProductId] = useState<number | null>(null);
  const [product, setProduct] = useState<Product>({
    name: '',
    description: '',
    price: 0,
    id_subcategory: 0,
    img: null,
  });
  const [products, setProducts] = useState<Product[]>([]);
  const navigate = useNavigate();
  const [errors, setErrors] = useState<Partial<Product>>({}); // Nuevo estado para mensajes de error
  const [formValid, setFormValid] = useState<boolean>(true);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;

    // Restablecer el mensaje de error para el campo actual
    setErrors({ ...errors, [name]: '' });

    // Validar el campo actual
    let isValid = true;

    if (name === 'price' && (isNaN(parseFloat(value)) || parseFloat(value) <= 0)) {
      setErrors({ ...errors, [name]: 'Ingrese un precio válido mayor que cero.' });
      isValid = false;
    } else if (['available_quantity', 'id_subcategory'].includes(name) && (isNaN(parseInt(value)) || parseInt(value) <= 0)) {
      setErrors({ ...errors, [name]: `Ingrese un valor válido para ${name}.` });
      isValid = false;
    }

    setFormValid(isValid);
    setProduct({ ...product, [name]: value });
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files) {
      setProduct({ ...product, img: e.target.files[0] });
    }
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setEditProductId(null);
    setProduct({
      name: '',
      description: '',
      price: 0,
      id_subcategory: 0,
      img: null,
    });
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        await fetchProducts();
      } catch (error) {
        console.error('Error al obtener productos:', error);
      }
    };

    fetchData();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get<Product[]>('http://localhost/ProyePet/public/api/Product_index');
      setProducts(response.data);
    } catch (error) {
      console.error('Error al obtener productos:', error);
    }
  };

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('name', product.name);
    formData.append('description', product.description);
    formData.append('price', product.price.toString());
    formData.append('id_subcategory', product.id_subcategory.toString());
    formData.append('img', product.img as Blob);

    try {
      await axios.post('http://localhost/ProyePet/public/api/Product_store', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          'Accept': 'application/json',
        },
      });

      fetchProducts();
      handleCloseModal();
    } catch (error) {
      console.error(error.response);
    }
  };

  const handleDeleteProduct = async (productId: number) => {
    try {
      await axios.post(`http://localhost/ProyePet/public/api/Product_destroy/${productId}`);
      fetchProducts();
    } catch (error) {
      console.error(error);
    }
  };

  const handleEditProduct = (productId: number) => {
    const selectedProduct = products.find((product) => product.id === productId);
    if (selectedProduct) {
      setProduct({ ...selectedProduct });
      setEditProductId(productId);
      setShowModal(true);
    }
  };

  const handleUpdateProduct = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();

    if (!product.name || !product.description || !product.price || !product.id_subcategory) {
      console.error('Please fill in all required fields.');
      return;
    }

    const formData = new FormData();
    formData.append('name', product.name);
    formData.append('description', product.description);
    formData.append('price', product.price.toString());
    formData.append('id_subcategory', product.id_subcategory.toString());

    if (product.img) {
      formData.append('img', product.img as Blob);
    }

    try {
      await axios.post(`http://localhost/ProyePet/public/api/Product_update/${editProductId}`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          'Accept': 'application/json',
        },
      });

      fetchProducts();
      handleCloseModal();
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <>
      <Button variant="success" onClick={() => setShowModal(true)}>
        Agregar Producto
      </Button>

      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>{editProductId ? 'Editar' : 'Agregar'} Producto</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          <Form onSubmit={editProductId ? handleUpdateProduct : handleSubmit}>
            <Form.Group className="mb-3">
              <Form.Label>Nombre:</Form.Label>
              <Form.Control type="text" name="name" value={product.name} onChange={handleChange} required />
              <Form.Text className="text-danger">{errors.name}</Form.Text>
              <br></br>
              <Form.Label>Descripcion:</Form.Label>
              <Form.Control type="text" name="description" value={product.description} onChange={handleChange} required />
              <Form.Text className="text-danger">{errors.description}</Form.Text>
              <br></br>
              <Form.Label>Precio:</Form.Label>
              <Form.Control type="number" name="price" value={product.price.toString()} onChange={handleChange} required />
              <Form.Text className="text-danger">{errors.price}</Form.Text>
              <br></br>
              <Form.Label>Subcategoría ID:</Form.Label>
              <Form.Control type="number" name="id_subcategory" value={product.id_subcategory.toString()} onChange={handleChange} required />
              <Form.Text className="text-danger">{errors.id_subcategory}</Form.Text>
              <br></br>
              <Form.Label>Imagen:</Form.Label>
              <Form.Control type="file" name="img" onChange={handleFileChange} required />
              <Button variant="secondary" onClick={handleCloseModal}>
                Cerrar
              </Button>
              <Button variant="primary" type="submit" disabled={!formValid}>
                {editProductId ? 'Actualizar Producto' : 'Agregar Producto'}
              </Button>
            </Form.Group>
          </Form>
        </Modal.Body>
      </Modal>

      <Table striped bordered hover>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nombre</th>
            <th>Descripción</th>
            <th>Precio</th>
            <th>Subcategoría ID</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product) => (
            <tr key={product.id}>
              <td>{product.id}</td>
              <td>{product.name}</td>
              <td>{product.description}</td>
              <td>{product.price}</td>
              <td>{product.id_subcategory}</td>
              <td>
                <Button variant="danger" onClick={() => handleDeleteProduct(product.id)}>
                  Eliminar
                </Button>
                <Button variant="warning" onClick={() => handleEditProduct(product.id)}>
                  Editar
                </Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
    </>
  );
