import React, { useState, useEffect } from 'react';
import { Modal, Button, View, Text, TextInput, StyleSheet, FlatList, Image } from 'react-native';
import axios from 'axios';

const Crud = () => {
  const [showModal, setShowModal] = useState(false);
  const [editProductId, setEditProductId] = useState<number | null>(null);
  const [product, setProduct] = useState({
    name: '',
    description: '',
    price: '',
    id_subcategory: '',
    size: '',
    color: '',
    available_quantity: '',
    img: null,
  });
  const [products, setProducts] = useState<any[]>([]);
  const [errors, setErrors] = useState<Record<string, string>>({});
  const [formValid, setFormValid] = useState(true);

  const handleChange = (name: string, value: string) => {
    setErrors({ ...errors, [name]: '' });

    let isValid = true;

    if (name === 'price' && (isNaN(parseFloat(value)) || parseFloat(value) <= 0)) {
      setErrors({ ...errors, [name]: 'Ingrese un precio v치lido mayor que cero.' });
      isValid = false;
    } else if (['id_subcategory'].includes(name) && (isNaN(parseInt(value)) || parseInt(value) <= 0)) {
      setErrors({ ...errors, [name]: `Ingrese un valor v치lido para ${name}.` });
      isValid = false;
    }

    setFormValid(isValid);
    setProduct({ ...product, [name]: value });
  };

  const handleFileChange = (img: any) => {
    setProduct({ ...product, img });
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setEditProductId(null);
    setProduct({
      name: '',
      description: '',
      price: '',
      id_subcategory: '',
      size: '',
      color: '',
      available_quantity: '',
      img: null,
    });
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost/Shaba/Shaba/public/api/Product_index');
        setProducts(response.data);
      } catch (error) {
        console.error('Error al obtener productos:', error);
      }
    };

    fetchData();
  }, []);

  const handleSubmit = async () => {
    const formData = new FormData();
    formData.append('name', product.name);
    formData.append('description', product.description);
    formData.append('price', product.price);
    formData.append('id_subcategory', product.id_subcategory);
    formData.append('size', product.size);
    formData.append('color', product.color);
    formData.append('available_quantity', product.available_quantity);
    formData.append('img', product.img);

    try {
      await axios.post('http://localhost/Shaba/Shaba/public/api/Product_store', formData);
      const response = await axios.get('http://localhost/Shaba/Shaba/public/api/Product_index');
      setProducts(response.data);
      handleCloseModal();
    } catch (error) {
      console.error(error.response);
    }
  };

  const handleDeleteProduct = async (productId: number) => {
    try {
      await axios.post(`http://localhost/Shaba/Shaba/public/api/Product_destroy/${productId}`);
      const response = await axios.get('http://localhost/Shaba/Shaba/public/api/Product_index');
      setProducts(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleEditProduct = (productId: number) => {
    const selectedProduct = products.find((product) => product.id === productId);
    setProduct({ ...selectedProduct });
    setEditProductId(productId);
    setShowModal(true);
  };

  const handleUpdateProduct = async () => {
    if (!product.name || !product.description || !product.price || !product.id_subcategory) {
      console.error('Please fill in all required fields.');
      return;
    }

    const formData = new FormData();
    formData.append('name', product.name);
    formData.append('description', product.description);
    formData.append('price', product.price);
    formData.append('id_subcategory', product.id_subcategory);
    formData.append('size', product.size);
    formData.append('color', product.color);
    formData.append('available_quantity', product.available_quantity);
    if (product.img) {
      formData.append('img', product.img);
    }

    try {
      await axios.post(`http://localhost/Shaba/Shaba/public/api/Product_update/${editProductId}`, formData);
      const response = await axios.get('http://localhost/Shaba/Shaba/public/api/Product_index');
      setProducts(response.data);
      handleCloseModal();
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <View style={styles.container}>
      <Button title="Agregar Producto" onPress={() => setShowModal(true)} />
      <Modal visible={showModal} onRequestClose={handleCloseModal}>
        {/* Contenido del modal */}
      </Modal>

      <FlatList
        data={products}
        keyExtractor={(item) => item.id.toString()}
        renderItem={({ item }) => (
          <View style={styles.itemContainer}>
            <Text>{item.id}</Text>
            <Text>{item.name}</Text>
            <Text>{item.description}</Text>
            <Text>{item.price}</Text>
            <Text>{item.id_subcategory}</Text>
            <Text>{item.size}</Text>
            <Text>{item.color}</Text>
            <Text>{item.available_quantity}</Text>
            <Image source={{ uri: `http://localhost/Shaba/Shaba/public/${item.img}` }} style={styles.image} />
            <View style={styles.buttonContainer}>
              <Button title="Eliminar" onPress={() => handleDeleteProduct(item.id)} />
              <Button title="Editar" onPress={() => handleEditProduct(item.id)} />
            </View>
          </View>
        )}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
  itemContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 10,
  },
  image: {
    width: 50,
    height: 50,
  },
  buttonContainer: {
    flexDirection: 'row',
  },
});

export default Crud;
