import React, { useState, useEffect } from 'react';
import { View, Text, TextInput, Button, FlatList, StyleSheet, TouchableOpacity } from 'react-native';
import axios from 'axios';

interface Product {
  id: number;
  name: string;
  description: string;
  price: number;
  id_subcategory: number;
  size?: string | null;
  color?: string | null;
  available_quantity: number;
  img: string;
}

const Crud: React.FC = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const [formData, setFormData] = useState<Product>({
    id: 0,
    name: '',
    description: '',
    price: 0,
    id_subcategory: 0,
    available_quantity: 0,
    img: '',
  });

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get('http://localhost/Shaba/Shaba/public/api/Product_index');
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const handleSubmit = async () => {
    // Validar el formulario antes de enviar los datos
    if (!validateProductForm(formData)) {
      return;
    }
    
    try {
      if (formData.id) {
        // Si hay un ID en formData, es una actualización
        await axios.put(`YOUR_BACKEND_URL/products/${formData.id}`, formData);
      } else {
        // Si no hay un ID, es una creación
        await axios.post('YOUR_BACKEND_URL/products', formData);
      }
      fetchProducts(); // Actualizar la lista de productos después de agregar o actualizar
      resetForm(); // Limpiar el formulario después de enviar
    } catch (error) {
      console.error('Error adding/updating product:', error);
    }
  };

  const handleEdit = (product: Product) => {
    // Al hacer clic en el botón de editar, establecer los datos del producto en el formulario
    setFormData(product);
  };

  const handleDelete = async (productId: number) => {
    try {
      await axios.delete(`YOUR_BACKEND_URL/products/${productId}`);
      fetchProducts(); // Actualizar la lista de productos después de eliminar
    } catch (error) {
      console.error('Error deleting product:', error);
    }
  };

  const resetForm = () => {
    // Limpiar el formulario
    setFormData({
      id: 0,
      name: '',
      description: '',
      price: 0,
      id_subcategory: 0,
      available_quantity: 0,
      img: '',
    });
  };

  const validateProductForm = (formData: Product): boolean => {
    if (!formData.name.trim() || formData.name.trim().length < 3) {
      alert('Por favor ingrese un nombre válido para el producto (mínimo 3 caracteres)');
      return false;
    }
    if (!formData.description.trim() || formData.description.trim().length < 10) {
      alert('Por favor ingrese una descripción válida para el producto (mínimo 10 caracteres)');
      return false;
    }
    if (formData.price <= 0 || isNaN(formData.price)) {
      alert('Por favor ingrese un precio válido para el producto (mayor que 0)');
      return false;
    }
    if (!formData.id_subcategory) {
      alert('Por favor seleccione una subcategoría para el producto');
      return false;
    }
    if (formData.available_quantity < 0 || isNaN(formData.available_quantity)) {
      alert('Por favor ingrese una cantidad válida para el producto (mayor o igual que 0)');
      return false;
    }
    // Puedes agregar más validaciones según tus necesidades
    return true;
  };

  return (
    <View style={styles.container}>
      <View style={styles.formContainer}>
        <Text style={styles.title}>Add/Edit Product</Text>
        <TextInput
          style={styles.input}
          placeholder="Name"
          value={formData.name}
          onChangeText={(text) => setFormData({ ...formData, name: text })}
        />
        {/* Repeat TextInput fields for other fields like description, price, etc. */}
        <Button title="Save Product" onPress={handleSubmit} />
        <Button title="Reset Form" onPress={resetForm} />
      </View>

      <View style={styles.productListContainer}>
        <Text style={styles.title}>Product List</Text>
        <FlatList
          data={products}
          keyExtractor={(item) => item.id.toString()}
          renderItem={({ item }) => (
            <View style={styles.productItem}>
              <Text>{item.name}</Text>
              {/* Display other product information */}
              <TouchableOpacity onPress={() => handleEdit(item)}>
                <Text>Edit</Text>
              </TouchableOpacity>
              <TouchableOpacity onPress={() => handleDelete(item.id)}>
                <Text>Delete</Text>
              </TouchableOpacity>
            </View>
          )}
        />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
    backgroundColor: '#fff',
  },
  formContainer: {
    marginBottom: 20,
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  input: {
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 10,
    paddingHorizontal: 10,
  },
  productListContainer: {
    flex: 1,
  },
  productItem: {
    paddingVertical: 10,
    borderBottomWidth: 1,
    borderBottomColor: '#ccc',
    flexDirection: 'row',
    justifyContent: 'space-between',
  },
});

export default Crud;
