import React, { useState, useEffect } from 'react';
import { View, Text, TextInput, Button, FlatList } from 'react-native';
import axios from 'axios';

interface Product {
  id: number;
  name: string;
  description: string;
  price: number;
  id_subcategory: number;
  size?: string;
  color?: string;
  available_quantity: number;
  img: string;
}

const ProductScreen: React.FC = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '',
    id_subcategory: '',
    size: '',
    color: '',
    available_quantity: '',
    img: '',
  });

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get('YOUR_BACKEND_URL/products');
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const handleSubmit = async () => {
    try {
      await axios.post('http://localhost/ProyePet/public/api/Product_index', formData);
      fetchProducts(); // Refresh the product list after adding a new product
    } catch (error) {
      console.error('Error adding product:', error);
    }
  };

  return (
    <View>
      <Text>Add Product</Text>
      <TextInput
        placeholder="Name"
        value={formData.name}
        onChangeText={(text) => setFormData({ ...formData, name: text })}
      />
      {/* Repeat TextInput fields for other fields like description, price, etc. */}
      <Button title="Add Product" onPress={handleSubmit} />

      <Text>Product List</Text>
      <FlatList
        data={products}
        keyExtractor={(item) => item.id.toString()}
        renderItem={({ item }) => (
          <View>
            <Text>{item.name}</Text>
            {/* Display other product information */}
          </View>
        )}
      />
    </View>
  );
};

export default ProductScreen;
