import React, { useState, useEffect } from 'react';
import { View, Text, TextInput, Button, FlatList, StyleSheet } from 'react-native';
import axios from 'axios';

interface Product {
  id: number;
  name: string;
  description: string;
  price: number;
  id_subcategory: number;
  size?: string;
  color?: string;
  available_quantity: number;
  img: string;
}

const Crud: React.FC = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '',
    id_subcategory: '',
    size: '',
    color: '',
    available_quantity: '',
    img: '',
  });

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get('http://localhost/Shaba/Shaba/public/api/Product_index');
      setProducts(response.data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const handleSubmit = async () => {
    try {
      await axios.post('YOUR_BACKEND_URL/products', formData);
      fetchProducts(); // Refresh the product list after adding a new product
      setFormData({
        name: '',
        description: '',
        price: '',
        id_subcategory: '',
        size: '',
        color: '',
        available_quantity: '',
        img: '',
      });
    } catch (error) {
      console.error('Error adding product:', error);
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.formContainer}>
        <Text style={styles.title}>Add Product</Text>
        <TextInput
          style={styles.input}
          placeholder="Name"
          value={formData.name}
          onChangeText={(text) => setFormData({ ...formData, name: text })}
        />
        {/* Repeat TextInput fields for other fields like description, price, etc. */}
        <Button title="Add Product" onPress={handleSubmit} />
      </View>

      <View style={styles.productListContainer}>
        <Text style={styles.title}>Product List</Text>
        <FlatList
          data={products}
          keyExtractor={(item) => item.id.toString()}
          renderItem={({ item }) => (
            <View style={styles.productItem}>
              <Text>{item.name}</Text>
              {/* Display other product information */}
            </View>
          )}
        />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
    backgroundColor: '#fff',
  },
  formContainer: {
    marginBottom: 20,
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 10,
  },
  input: {
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 10,
    paddingHorizontal: 10,
  },
  productListContainer: {
    flex: 1,
  },
  productItem: {
    paddingVertical: 10,
    borderBottomWidth: 1,
    borderBottomColor: '#ccc',
  },
});

export default Crud;
