import React from 'react';
import { View, Text, StyleSheet, Image, TouchableOpacity, ImageBackground } from 'react-native';
import { NativeStackNavigationProp } from '@react-navigation/native-stack';

type ScreenNames = 'SignIn' | 'SignUp';

type NavigationProp = NativeStackNavigationProp<{
  SignIn: undefined;
  SignUp: undefined;
}>;

interface Props {
  navigation: NavigationProp;
}

const Home: React.FC<Props> = ({ navigation }) => {
  const goToScreen = (screenName: ScreenNames) => {
    navigation.navigate(screenName);
  };

  return (
    <ImageBackground source={require('../images/background.jpg')} style={styles.background}>
      <View style={styles.container}>
        <Image source={require('../images/shaba.png')} style={styles.logo} />
        <Text style={styles.title}>Welcome to SHABA!</Text>
        <TouchableOpacity style={styles.button} onPress={() => goToScreen('SignIn')}>
          <Text style={styles.buttonText}>Sign In</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.button} onPress={() => goToScreen('SignUp')}>
          <Text style={styles.buttonText}>Sign Up</Text>
        </TouchableOpacity>
      </View>
    </ImageBackground>
  );
};

const styles = StyleSheet.create({
  background: {
    flex: 1,
    resizeMode: 'cover',
    justifyContent: 'center',
  },
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  logo: {
    width: 150,
    height: 150,
    marginBottom: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 30,
    color: '#fff',
  },
  button: {
    backgroundColor: '#4CAF50',
    width: '50%',
    padding: 15,
    borderRadius: 5,
    alignItems: 'center',
    marginBottom: 10,
  },
  buttonText: {
    color: '#fff',
    fontSize: 18,
  },
});

export default Home;
